	(27, 'Transfers', 'Weekly transfer times', 'calldev', 'SELECT hour( contact ) , \'McDowall\', count( * )\r\nFROM `hist-dec13mcdowallhome` h\r\nLEFT JOIN `db-dec13mcdowallhome` d ON h.urn = d.urn\r\nWHERE h.`exit` LIKE \'transfer%\'\r\nAND date( lastcall ) >= date( date_sub( curdate( ) , INTERVAL 7\r\nDAY ) )\r\nGROUP BY 1\r\nunion\r\nSELECT hour( contact ) , \'SLS\', count( * )\r\nFROM `hist-dec13slshousehold` h\r\nLEFT JOIN `db-dec13slshousehold` d ON h.urn = d.urn\r\nWHERE h.`exit` LIKE \'transfer%\'\r\nAND date( lastcall ) >= date( date_sub( curdate( ) , INTERVAL 7\r\nDAY ) )\r\nGROUP BY 1\r\nunion\r\nSELECT hour( contact ) , \'TPL\', count( * )\r\nFROM `hist-dec13tplhome` h\r\nLEFT JOIN `db-dec13tplhome` d ON h.urn = d.urn\r\nWHERE h.`exit` LIKE \'transfer%\'\r\nAND date( lastcall ) >= date( date_sub( curdate( ) , INTERVAL 7\r\nDAY ) )\r\nGROUP BY 1\r\nunion\r\nSELECT hour( contact ) , \'Phruit\', count( * )\r\nFROM `hist-dec13phruithome` h\r\nLEFT JOIN `db-dec13phruithome` d ON h.urn = d.urn\r\nWHERE h.`exit` LIKE \'transfer%\'\r\nAND date( lastcall ) >= date( date_sub( curdate( ) , INTERVAL 7\r\nDAY ) )\r\nGROUP BY 1', '1', '20', '*', '*', '5', '0', 'exports', ''),
	(26, 'Multiple Tables', 'Example of multiple tabels in one report', 'calldev', 'select urn, contact, `exit`, \'household\' from `hist-oct13household` where date(contact) = curdate() and `exit` = \'Transfer\' union\r\nselect urn, contact, `exit`, \'consumer\' from `hist-oct13consumer` where date(contact) = curdate() and `exit` = \'Transfer\'', '1', '*', '*', '1', '*', '0', 'exports', ''),
	(29, 'Monthly - Source export', 'Suppression', 'calldev', 'select source from `scripts`', '30', '8', '26', '*', '*', '0', 'exports', ''),
	(54, 'The Mark Group Appointments', 'Full data export for bookings', 'calldev', 'select urn, 	coname, 	add1 ,	add2 ,	add3 ,	town ,	county ,	postcode ,	phone ,	title ,	forename ,	surname, 	position ,	email 	,website ,	empband ,	siccode ,	actdesc, 	sicdesc ,	turnover, 	sales_exec 	,m_email, op_hours, 	light_type, 	light_height ,	environment ,	light_fitted ,	lastcall ,dials,apptdate,`exit`\r\nfrom `db-feb14mglightingdivision` where `exit` = \'Appointment made\' order by lastcall desc;\r\n\r\nselect urn, 	coname, 	add1 ,	add2 ,	add3 ,	town ,	county ,	postcode ,	phone ,	title ,	forename ,	surname, 	position ,	email 	,website ,	empband ,	siccode ,	actdesc, 	sicdesc ,	turnover, 	sales_exec 	,m_email, op_hours, 	light_type, 	light_height ,	environment ,	light_fitted ,	lastcall ,dials,apptdate,`exit`\r\nfrom `db-apr14mglightingdivision` where `exit` = \'Appointment made\' order by lastcall desc;', '1', '5', '*', '*', '5', '0', 'exports', ''),
