	(60, 'Exel - Shaun appointments', 'Weekly - All outcomes', 'calldev', 'select lead,`exit`,lastcall,company,add1,add2,add3,town,county,postcode,phone,altphone,mobile,email,software,nodrivers,title,forename,surname,softwarecap,system,dm_title,dm_forename,dm_surname,callback,democomments,nextcall,dials,advisor,comments,urn from `db-mar14exelshaun` WHERE date( lastcall ) >= date( date_sub( curdate( ) , INTERVAL 7\r\nDAY ) ) and nextcall is null and lastcall is not null', '30', '9', '*', '*', '1', '0', 'exports', ''),
	(61, 'FSB', 'In Progress Records', 'calldev', 'SELECT manager, substring_index( postcode, \' \', 1 ) , count( * )\r\nFROM `db-jan14fsbcolin`\r\nWHERE nextcall IS NOT NULL\r\nAND `exit` NOT LIKE \'%dm%\'\r\nAND `exit` NOT LIKE \'%interest%\'\r\nAND `exit` NOT LIKE \'%info%\'\r\nAND dials <5\r\nGROUP BY 1 , 2\r\nunion\r\nSELECT manager, substring_index( postcode, \' \', 1 ) , count( * )\r\nFROM `db-jan14fsbdawn`\r\nWHERE nextcall IS NOT NULL\r\nAND `exit` NOT LIKE \'%dm%\'\r\nAND `exit` NOT LIKE \'%interest%\'\r\nAND `exit` NOT LIKE \'%info%\'\r\nAND dials <5\r\nGROUP BY 1 , 2\r\nunion\r\nSELECT manager, substring_index( postcode, \' \', 1 ) , count( * )\r\nFROM `db-jan14fsbdiane`\r\nWHERE nextcall IS NOT NULL\r\nAND `exit` NOT LIKE \'%dm%\'\r\nAND `exit` NOT LIKE \'%interest%\'\r\nAND `exit` NOT LIKE \'%info%\'\r\nAND dials <5\r\nGROUP BY 1 , 2\r\nunion\r\nSELECT manager, substring_index( postcode, \' \', 1 ) , count( * )\r\nFROM `db-jan14fsbmark`\r\nWHERE nextcall IS NOT NULL\r\nAND `exit` NOT LIKE \'%dm%\'\r\nAND `exit` NOT LIKE \'%interest%\'\r\nAND `exit` NOT LIKE \'%info%\'\r\nAND dials <5\r\nGROUP BY 1 , 2\r\nunion\r\nSELECT manager, substring_index( postcode, \' \', 1 ) , count( * )\r\nFROM `db-jan14fsbpeter`\r\nWHERE nextcall IS NOT NULL\r\nAND `exit` NOT LIKE \'%dm%\'\r\nAND `exit` NOT LIKE \'%interest%\'\r\nAND `exit` NOT LIKE \'%info%\'\r\nAND dials <5\r\nGROUP BY 1 , 2\r\nunion\r\nSELECT manager, substring_index( postcode, \' \', 1 ) , count( * )\r\nFROM `db-jan14fsbsue`\r\nWHERE nextcall IS NOT NULL\r\nAND `exit` NOT LIKE \'%dm%\'\r\nAND `exit` NOT LIKE \'%interest%\'\r\nAND `exit` NOT LIKE \'%info%\'\r\nAND dials <5\r\nGROUP BY 1 , 2\r\nunion\r\nSELECT manager, substring_index( postcode, \' \', 1 ) , count( * )\r\nFROM `db-jan14fsbted`\r\nWHERE nextcall IS NOT NULL\r\nAND `exit` NOT LIKE \'%dm%\'\r\nAND `exit` NOT LIKE \'%interest%\'\r\nAND `exit` NOT LIKE \'%info%\'\r\nAND dials <5\r\nGROUP BY 1 , 2\r\nunion\r\nSELECT manager, substring_index( postcode, \' \', 1 ) , count( * )\r\nFROM `db-jan14fsbvictor`\r\nWHERE nextcall IS NOT NULL\r\nAND `exit` NOT LIKE \'%dm%\'\r\nAND `exit` NOT LIKE \'%interest%\'\r\nAND `exit` NOT LIKE \'%info%\'\r\nAND dials <5\r\nGROUP BY 1 , 2\r\nunion\r\nSELECT manager, substring_index( postcode, \' \', 1 ) , count( * )\r\nFROM `db-jan14fsbadam`\r\nWHERE nextcall IS NOT NULL\r\nAND `exit` NOT LIKE \'%dm%\'\r\nAND `exit` NOT LIKE \'%interest%\'\r\nAND `exit` NOT LIKE \'%info%\'\r\nAND dials <5\r\nGROUP BY 1 , 2\r\nunion\r\nSELECT manager, substring_index( postcode, \' \', 1 ) , count( * )\r\nFROM `db-jan14fsbchris`\r\nWHERE nextcall IS NOT NULL\r\nAND `exit` NOT LIKE \'%dm%\'\r\nAND `exit` NOT LIKE \'%interest%\'\r\nAND `exit` NOT LIKE \'%info%\'\r\nAND dials <5\r\nGROUP BY 1 , 2\r\nunion\r\nSELECT manager, substring_index( postcode, \' \', 1 ) , count( * )\r\nFROM `db-jan14fsbchris`\r\nWHERE nextcall IS NOT NULL\r\nAND `exit` NOT LIKE \'%dm%\'\r\nAND `exit` NOT LIKE \'%interest%\'\r\nAND `exit` NOT LIKE \'%info%\'\r\nAND dials <5\r\nGROUP BY 1 , 2\r\nunion\r\nSELECT manager, substring_index( postcode, \' \', 1 ) , count( * )\r\nFROM `db-jan14fsbrick`\r\nWHERE nextcall IS NOT NULL\r\nAND `exit` NOT LIKE \'%dm%\'\r\nAND `exit` NOT LIKE \'%interest%\'\r\nAND `exit` NOT LIKE \'%info%\'\r\nAND dials <5\r\nGROUP BY 1 , 2', '1', '16', '*', '*', '1,2,3,4,5', '0', 'exports', ''),
	(62, 'Prospector CAE Appointments', 'Weekly appointments set by BDEs', 'clmsdev', 'SELECT id,date_format( events.date_added, \'%d/%m/%y\' ) AS date_added, date_format( begin_date, \'%d/%m/%y\' ) AS appointment_date, set_by AS bde, coname AS company, events.urn, attendee AS cae\r\nFROM events LEFT JOIN events_attendees ON events_attendees.event_id = events.id\r\nLEFT JOIN leads ON events.urn = leads.urn\r\nWHERE set_by in(select login from users where rep_group = \'Prospects\') \r\nAND `status` = \'Live\'\r\nAND date(events.date_added) >= subdate(curdate(),interval 7 day) and cancelled is null', '1', '6', '*', '*', '1', '1', 'exports', ''),
	(63, 'Prospector Renewal Dates', 'weekly captured renewal dates', 'clmsdev', 'select date_format(date(ro.date_added),\'%d/%m/%y\') as date_added,date_format(date(r.date),\'%d/%m/%y\') as renewal_date,\r\nro.prospector as bde,coname as company, `type` as product,\r\nr.urn from renewal_ownership ro left join renewals r on renewal_id = r.id left join leads on r.urn = leads.urn \r\nwhere date(ro.date_added) >= subdate(curdate(),interval 7 day)', '49', '6', '*', '*', '1', '1', 'exports', ''),
	(64, 'Prospector CAE Feedback', 'Weekly cae appintment feedback', 'clmsdev', 'SELECT id,date_format(begin_date,\'%d/%m/%y\') as app_date,date_format(`events_feedback`.date_added,\'%d/%m/%y\') as feedback_date,urn,cae,bde,question,value as score,score as total_score,\r\nreason FROM `events_feedback` left join events_feedback_answers using(id) left join events using(id) left join leads using(urn) WHERE date(events_feedback.date_added) >= subdate(curdate(),interval 7 day)', '45', '6', '*', '*', '1', '1', 'exports', ''),
	(65, 'Exel', 'Interested in Software', 'calldev', 'SELECT lead, company,contact,phone,email,jobtitle,q_companysector,q_useie,q_noie,q_employees,q_empreview,q_appraisal,q_onlinecap,q_process,q_implement,q_demo FROM `db-apr14exelemPerform` where `exit` =  \'Interested in software\' and date(lastcall) = curdate();', '1', '17', '*', '*', '1,2,3,4,5', '0', 'exports', ''),
	(66, 'Exel', 'Email info', 'calldev', 'SELECT lead, company,contact,phone,email,jobtitle,q_companysector,q_useie,q_noie,q_employees,q_empreview,q_appraisal,q_onlinecap,q_process,q_implement,q_demo FROM `db-apr14exelemPerform` where `exit` = \'Email info\' and date(lastcall) = curdate();', '1', '17', '*', '*', '1,2,3,4,5', '0', 'exports', ''),
