	(68, 'PL Capture Test', 'Testing the CPH PL Capture exports', 'clmsdev', 'SELECT product, renewal, \'n/a\' AS clientkey, title, firstname, lastname, dob, phone, add1 AS address_1, town AS town, postcode, gender, user AS \'added_by\', cpl.date_added AS date_added\r\nFROM captured_personal_lines cpl\r\nLEFT JOIN captured_personal_details\r\nUSING ( id )\r\nWHERE renewal IS NOT NULL\r\nUNION SELECT prodtarget, renewal, clientkey, title, firstname, lastname, dob, phone, add1 AS address_1, town, postcode, gender, user AS \'added_by\', ap.updated AS date_added\r\nFROM acturis_personal ap\r\nLEFT JOIN captured_personal_details\r\nUSING ( clientkey )\r\nWHERE firstname IS NOT NULL\r\nAND renewal IS NOT NULL', '1', '1', '*', '*', '1', '0', 'exports', ''),
